INSERT INTO `tbl_page` VALUES (20, 'Syarat dan Ketentuan', '<p><span style=\"font-size:16px\"><span style=\"color:black\">KEBIJAKAN INVESTASI</span></span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:16px\"><span style=\"color:black\">presentase (%) yang dimunculkan bersifat p.a (per anom) atau per tahun namun pengembalian dana pokok investasi sesuai tenor yang tercantum</span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"color:black\">Pengembalian bunga dilakukan setiap bulan terhitung sejak masa kampanye habis</span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"color:black\">Apabila setelah jatuh tempo atau waktu masa kampanye pendanaan telah berakhir namun nilai investasi tidak memenuhi kebutuhan pengaju dana/peminjam maka dana investasi dikembalikan kepada Investor maksimal 3 hari kerja</span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"color:black\">Apabila sebelum jatuh tempo atau waktu masa kampanye pendanaan berakhir dan nilai investasi memenuhi kebutuhan maka Pengembalian bunga dilakukan di bulan selanjutnya setiap bulan sesuai tanggal investasi dilakukan oleh investor</span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"color:black\">Apabila kebutuhan investasi memenuhi H-7 hingga hari terakhir pendanaan/investasi maka Pengembalian bunga dilakukan di bulan selanjutnya setiap bulan sesuai tanggal investasi dilakukan oleh investor</span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"color:black\">Pengembalian dana pokok investasi dilakukan sesuai kebijakan penarikan dana investasi yang mengendap</span></span></li>\r\n</ol>\r\n\r\n<p>&nbsp;</p>\r\n', '-', '-', '-', 'invest/page/syarat-ketentuan', 'footer', '', '0', 3);
