INSERT INTO `tbl_page` VALUES (22, '', '<p><span style=\"font-size:16px\">Risiko Penerima Pinjaman</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Sebagai penerima pinjaman, Anda bertanggung jawab untuk melakukan pembayaran pokok dan bunga pinjaman Anda. Sesuai Pasal 1131 KUHPerdata, setiap aset milik penerima pinjaman menjadi jaminan untuk setiap perikatan yang dibuat. Dengan demikian, dalam hal Anda gagal melakukan pembayaran, Anda dapat kehilangan aset atau harta kekayaan Anda. Pastikan Anda meminjam sesuai kemampuan Anda.<br />\r\n<br />\r\nProses penagihan pinjaman gagal bayar dilakukan Invest Pro&nbsp;dengan proses sebagai berikut:</span></p>\r\n\r\n<ol>\r\n	<li style=\"text-align:justify\" value=\"1.\"><span style=\"font-size:14px\">Gagal bayar 1-90 hari setelah jatuh tempo</span>\r\n\r\n	<p><span style=\"font-size:14px\">Dalam periode ini tim desk collection dan field collection Invest Pro&nbsp;akan menghubungi dan menemui Anda untuk menagih kewajiban pembayaran Anda yang telah lewat tanggal jatuh tempo.</span></p>\r\n	</li>\r\n	<li style=\"text-align:justify\" value=\"2.\"><span style=\"font-size:14px\">Gagal bayar lewat 90 hari setelah jatuh tempo</span>\r\n	<p><span style=\"font-size:14px\">Dalam periode ini Invest Pro&nbsp;akan menunjuk kuasa hukum atau jasa penagihan pihak ketiga. Dalam hal Invest Pro&nbsp;menggunakan kuasa hukum, maka kuasa hukum tersebut dapat mengajukan eksekusi agunan, permohonan pailit, gugatan perdata, ataupun memasukkan laporan polisi dalam hal terdapat indikasi tindak pidana. Dalam hal Invest Pro&nbsp;menunjuk jasa penagihan pihak ketiga, jasa penagih tersebut akan menghubungi dan menemui Anda untuk menagih kewajiban pembayaran Anda.</span></p>\r\n	</li>\r\n</ol>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Dengan menerima pinjaman melalui Invest Pro, penerima pinjaman memberikan konfirmasi bahwa:</span></p>\r\n\r\n<ol>\r\n	<li style=\"text-align:justify\" value=\"1.\"><span style=\"font-size:14px\">Penerima Pinjaman sudah mengetahui dan menanggung sepenuhnya risiko atas penerimaan pinjaman;</span></li>\r\n	<li style=\"text-align:justify\" value=\"2.\"><span style=\"font-size:14px\">Penerima Pinjaman sudah mengetahui risiko kehilangan aset ataupun harta kekayaaan akibat gagal bayar;</span></li>\r\n	<li style=\"text-align:justify\" value=\"3.\"><span style=\"font-size:14px\">Penerima Pinjaman sudah mengetahui, mempertimbangkan dan menyetujui segala tingkat bunga dan biaya yang berlaku;</span></li>\r\n	<li style=\"text-align:justify\" value=\"4.\"><span style=\"font-size:14px\">Penerima Pinjaman telah mempelajari dan memiliki pengetahuan dasar mengenai layanan pinjam meminjam uang berbasis teknologi informasi sebelum menerima pinjaman;</span></li>\r\n	<li style=\"text-align:justify\" value=\"5.\"><span style=\"font-size:14px\">Catatan kredit Penerima Pinjaman akan dilaporkan secara berkala kepada Otoritas Jasa Keuangan dan Asosiasi Fintech Pendaan Bersama Indonesia untuk kepentingan Pusat Data Fintech Lending yang akan dimanfaatkan bersama dengan para pelaku industri perbankan nasional dan industri keuangan lainnya;</span></li>\r\n	<li style=\"text-align:justify\" value=\"6.\"><span style=\"font-size:14px\">Penerima Pinjaman sudah membaca dan mempelajari setiap syarat dan ketentuan.</span></li>\r\n</ol>\r\n', 'risiko-penerima-pinjaman', 'risiko-penerima-pinjaman', 'risiko-penerima-pinjaman', 'risiko-penerima-pinjaman', 'footer', 'fa fa-user', '0', 0);
INSERT INTO `tbl_page` VALUES (23, '', '<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"color:#5c5c5c\">Kriteria Penilaian</span></strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\">7 Pilar<span style=\"font-size:14px\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:#5c5c5c\"> Pro &nbsp;melakukan penilaian kelayakan dan risiko pinjaman pelaku usaha dengan internal credit scoring system yang fokus kepada 3 hal di bawah ini:</span></span></span></span></p>\r\n\r\n<ol>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Keuangan</strong></span></span><br />\r\n	7 Pilar<span style=\"font-size:14px\"><span style=\"font-family:Calibri,sans-serif\"> Pro &nbsp;melakukan validasi atas aspek keuangan dengan melakukan cross check dengan rekening koran yang diberikan peminjam atau riwayat transaksi penjualan di platform online tempat calon peminjam berjualan. Setelah kondisi keuangan divalidasi, </span></span>7 Pilar<span style=\"font-size:14px\"><span style=\"font-family:Calibri,sans-serif\"> diberikan juga menjadi faktor yang dipertimbangkan dalam penilaian risiko dan penentuan bunga pinjaman. Agunan bisa berupa tagihan dari Invoice/PO/SPK/Kontrak, berupa persediaan dagang, peralatan, maupun tanah dan bangunan.</span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Credit Behaviour</strong><br />\r\n	Bekerja sama dengan biro kredit Pefindo, </span></span>7 Pilar<span style=\"font-size:14px\"><span style=\"font-family:Calibri,sans-serif\"> Pro &nbsp;melakukan analisis kuantitatif dan kualitatif berupa pengecekan rekam jejak dan credit behaviour peminjam. </span></span>7 Pilar<span style=\"font-size:14px\"><span style=\"fon