INSERT INTO `tbl_page` VALUES (27, '', '<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Auto lending merupakan fitur untuk memberikan investasi secara otomatis yang bertujuan untuk memudahkan lender tanpa harus menghabiskan waktu dalam mencari peluang investasi yang sesuai dengan preferensi.</span></strong></span></span></p>\r\n\r\n<ol>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Untuk menggunakan fitur Auto Lending, kamu dapat menentukan kriteria investasi yang ingin didanai secara otomatis berdasarkan:</span></span></span>\r\n\r\n	<ol>\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Rating dan bunga investasi ;</span></span></span></li>\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Tenor investasi ;</span></span></span></li>\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Ada tidaknya agunan; dan</span></span></span></li>\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Jumlah pendanaan / bid per investasi .</span></span></span></li>\r\n	</ol>\r\n	</li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Kamu juga dapat menentukan limit jumlah total pendanaan Auto Lending (&ldquo;Limit Auto Lending&rdquo;).</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Jumlah maksimal Auto Lending yang aktif secara bersamaan adalah 3 Auto Lending. Silakan menonaktifkan salah satu Auto Lending yang sedang aktif apabila ingin menggantinya dengan Auto Lending yang baru.</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Dana yang terdapat pada akun virtual pemberian investasi kamu akan secara otomatis 7 Pilar Pro tempatkan pada setiap investasi yang sesuai dengan kriteria yang kamu tentukan, dengan ketentuan sebagai berikut:</span></span></span>\r\n	<ol>\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Dana Auto Lending akan ditempatkan:</span></span></span>\r\n		<ol>\r\n			<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">untuk investasi baru yang diluncurkan yang sesuai dengan kriteria yang kamu tentukan, ketika investasi baru tersebut diluncurkan;</span></span></span></li>\r\n			<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">untuk investasi yang telah diluncurkan sebelum fitur Auto Lending diaktifkan, pada pukul 00.00 di hari berikutnya.</span></span></span></li>\r\n		</ol>\r\n		</li>\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Apabila jumlah yang masih dapat didanai dari suatu investasi kurang dari jumlah bid per investasi yang sudah kamu tentukan (termasuk sebagai akibat dari adanya bid Auto Lending dari para pengguna lainnya atas investasi yang sama), maka jumlah pendanaan kamu akan menjadi lebih kecil secara proporsional;</span></span></span></li>\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Apabila dana atau sisa Limit Auto Lending lebih kecil dari jumlah bid per investasi yang sudah kamu tentukan, maka jumlah pendanaan disesuaikan menjadi sebesar dana atau sisa Limit Auto Lending kamu, tergantung jumlah mana yang lebih kecil;</span></span></span></li>\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Satu peluang investasi hanya dapat dilakukan 1x bid Auto Lending oleh akunmu. Apabila terdapat lebih dari 1 bid Auto Lending atas 1 investasi yang sama oleh akunmu, maka digunakan bid dari Auto Lending yang terlebih dahulu kamu buat;</span></span></span></li>\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Auto Lending hanya akan aktif apabila akun kamu mempunyai dana minimal sebesar Rp100.000 dan sisa Limit Auto Lending minimal sebesar Rp100.000.</span></span></span></li>\r\n	</ol>\r\n	</li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Kamu menyetujui bahwa setiap pendanaan yang dilakukan secara otomatis melalui fitur Auto Lending ini mengikat dan tidak dapat dibatalkan. Namun, kamu dapat memilih untuk mengaktifkan atau menonaktifkan fitur Auto Lending pada akunmu.</span></span></span></li>\r\n</ol>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n', '', '', '', '', 'footer', 'fa fa-user', '1', 0);
