INSERT INTO `tbl_page` VALUES (32, 'Cara Investasi', '<p><img src=\"https://www.bizhare.id/assets/icons-bersama@3x.png\" /></p>\r\n\r\n<p>INVESTASI BISNIS BERSAMA</p>\r\n\r\n<p>Kamu dapat membuka bisnis Waralaba secara bersama-sama dan mendapatkan Legalitas PT.</p>\r\n\r\n<p><img src=\"https://www.bizhare.id/assets/icons-laporan@3x.png\" /></p>\r\n\r\n<p>LAPORAN KEUANGAN BULANAN</p>\r\n\r\n<p>Kamu akan mendapatkan laporan bisnis setiap bulan secara langsung melalui Website dan Apps Bizhare. Sehingga aman dan transparan.</p>\r\n\r\n<p><img src=\"https://www.bizhare.id/assets/icons-distribus@3x.png\" /></p>\r\n\r\n<p>DISTRIBUSI PROFIT BERKALA</p>\r\n\r\n<p>Pembagian profit sesuai persentase kepemilikan saham secara otomatis melalui sistem Saldo Bizhare.</p>\r\n', 'cara-investasi', 'cara-investasi', 'cara-investasi', 'invest/page/cara-investasi', 'header', '-', '0', 0);
INSERT INTO `tbl_page` VALUES (37, 'Tentang Kami', '<p><span style=\"font-size:14px\">Tentang SEVENPILAR</span></p>\r\n\r\n<p><span style=\"font-size:14px\">SEVENPILAR&nbsp;merupakan layanan digital investasi yang berlokasi di Komplek Ruko Rose Garden , Jalan Rose garden 3 Nomor 27 &ndash; Lantai 2 , Kota Bekasi &ndash; Jawa Barat.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Kami melayani kebutuhan untuk pengembangan bisnis industri properti. Layanan kami menyediakan platform digital investasi di sektor bisnis yang berkaitan dengan industri properti atau <em>real estate. </em>Dengan skema bagi hasil laba yang saling menguntung untuk bersama-sama maju secara bisnis dan secara keuangan.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>VISI SEVENPILAR</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Menjadi motor untuk menggerakkan roda ekonomi khususnya sektor industri yang berkaitan dengan dunia properti.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Menjadi motor bagi para investor untuk mencapai peningkatan keuangan dengan investasi yang terpercaya, aman dan amanah</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>MISI SEVENPILAR</strong></span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:14px\">Menyediakan layanan platform digital investasi properti berupa aplikasi berbasis web dan mobile.</span></li>\r\n	<li><span style=\"font-size:14px\">Menyelenggarakan layanan investasi properti dengan memaksimalkan kemajuan Teknologi Informasi dan Komunikasi.</span></li>\r\n	<li><span style=\"font-size:14px\">Mendorong semua lapisan masyarakat untuk berperan aktif dalam aktivitas investasi di sektor properti agar tercapai perekonomian yang makmur dan sejahtera.</span></li>\r\n	<li><span style=\"font-size:14px\">Membangun hubungan kerjasama yang saling menguntungkan dengan para pelaku bisnis yang berkaitan dengan bisnis properti.</span></li>\r\n	<li><span style=\"font-size:14px\">Memiliki peran aktif mengembangkan platform investasi properti dengan didukung sumber daya manusia yang ahli dan terampil sesuai dengan kebutuhan di sektor properti.</span></li>\r\n</ol>\r\n', 'tentang-kami', 'tentang-kami', 'tentang-kami', 'tentang-kami', 'footer', 'fa fa-user', '0', 1);
INSERT INTO `tbl_page` VALUES (39, 'PERHATIAN', '<ol>\r\n	<li style=\"text-align: justify;\">Layanan Pinjam Meminjam Berbasis Teknologi Informasi merupakan kesepakatan perdata antara Pemberi Investasi dengan Penerima Investasi, sehingga segala risiko yang timbul dari kesepakatan tersebut ditanggung sepenuhnya oleh masing-masing pihak.</li>\r\n	<li style=\"text-align: justify;\">Resiko Kredit atau Gagal Bayar dan seluruh kerugian dari atau terkait dengan kesepakatan pinjam meminjam ditanggung sepenuhnya oleh Pemberi Investasi. Tidak ada lembaga atau otoritas negara yang bertanggung jawab atas resiko gagal bayar dan kerugian tersebut.</li>\r\n	<li style=\"text-align: justify;\">Penyelenggara dengan persetujuan dari masing-masing Pengguna (Pemberi Investasi dan/atau Penerima Investasi ) mengakses, memperoleh, menyimpan, mengelola dan/atau menggunakan data pribadi Pengguna (&quot;Pemanfaatan Data&quot;) pada atau di dalam benda, perangkat elektronik (termasuk smartphone atau telepon seluler), perangkat keras (hardware) maupun lunak (software), dokumen elektronik, aplikasi atau sistem elektronik milik Pengguna atau yang dikuasai Pengguna, dengan memberitahukan tujuan, batasan dan mekanisme Pemanfaatan Data tersebut kepada Pengguna yang bersangkutan sebelum memperoleh persetujuan yang dimaksud.</li>\r\n	<li style=\"text-align: justify;\">Pemberi Investasi yang belum memiliki pengetahuan dan pengalaman pinjam meminjam, disarankan untuk tidak menggunakan layanan ini.</li>\r\n	<li style=\"text-align: justify;\">Penerima Investasi harus mempertimbangkan tingkat bunga Investasi dan biaya lainnya sesuai dengan kemampuan dalam melunasi Investasi .</li>\r\n	<li style=\"text-align: justify;\">Setiap kecurangan tercatat secara digital di dunia maya dan dapat diketahui masyarakat luas di media sosial.</li>\r\n	<li style=\"text-align: justify;\">Pengguna harus membaca dan memahami informasi ini sebelum membuat keputusan menjadi Pemberi Investasi atau Penerima Investasi .</li>\r\n	<li style=\"text-align: justify;\">Pemerintah yaitu dalam hal ini Otoritas Jasa Keuangan,tidak bertanggung jawab atas setiap pelanggaran atau ketidakpatuhan oleh Pengguna,baik Pemberi Investasi maupun Penerima Investasi (baik karena kesengajaan atau kelalaian Pengguna)terhadap ketentuan peraturan perundang-undangan maupun kesepakatan atau perikatan antara Penyelenggara dengan Pemberi Investasi dan/atau Penerima Investasi .</li>\r\n</ol>\r\n', '-', '-', '-', 'perhatian', 'perhatian', '-', '0', 0);
